import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';
import { ChevronRightIcon, CodeBracketIcon } from '@heroicons/react/24/outline';

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;

    const userMessage = { type: 'user', content: input };
    setMessages(prev => [...prev, userMessage]);
    setInput('');
    setIsLoading(true);

    try {
      const response = await axios.post('http://localhost:8000/api/chat', {
        message: input
      });

      const botMessage = {
        type: 'bot',
        content: response.data.answer,
        sql: response.data.sql_query,
        data: response.data.data,
        showSql: false
      };

      setMessages(prev => [...prev, botMessage]);
    } catch (error) {
      const errorMessage = {
        type: 'error',
        content: 'Sorry, there was an error processing your request.'
      };
      setMessages(prev => [...prev, errorMessage]);
    }

    setIsLoading(false);
  };

  const toggleSql = (index) => {
    setMessages(messages.map((msg, i) => {
      if (i === index) {
        return { ...msg, showSql: !msg.showSql };
      }
      return msg;
    }));
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-white shadow-sm">
        <div className="max-w-7xl mx-auto px-4 py-4">
          <h1 className="text-2xl font-bold text-gray-900">Talk2Tables</h1>
          <p className="text-sm text-gray-500">Chat with your database using natural language</p>
        </div>
      </div>

      {/* Chat Container */}
      <div className="max-w-7xl mx-auto px-4 py-8">
        <div className="bg-white rounded-lg shadow-lg min-h-[600px] flex flex-col">
          {/* Messages Area */}
          <div className="flex-1 p-4 overflow-y-auto">
            {messages.map((message, index) => (
              <div key={index} className={`mb-4 ${message.type === 'user' ? 'text-right' : ''}`}>
                <div className={`inline-block max-w-[80%] ${
                  message.type === 'user' 
                    ? 'bg-blue-600 text-white rounded-l-lg rounded-br-lg' 
                    : 'bg-gray-100 text-gray-900 rounded-r-lg rounded-bl-lg'
                } p-3`}>
                  <p>{message.content}</p>

                  {message.type === 'bot' && message.data && (
                    <div className="mt-2">
                      <button
                        onClick={() => toggleSql(index)}
                        className="flex items-center text-sm text-gray-500 hover:text-gray-700"
                      >
                        <CodeBracketIcon className="h-4 w-4 mr-1" />
                        {message.showSql ? 'Hide SQL' : 'Show SQL'}
                      </button>

                      {message.showSql && (
                        <pre className="mt-2 p-2 bg-gray-800 text-gray-200 rounded text-sm overflow-x-auto">
                          {message.sql}
                        </pre>
                      )}

                      <div className="mt-2 overflow-x-auto">
                        <table className="min-w-full divide-y divide-gray-200">
                          <thead className="bg-gray-50">
                            {message.data.length > 0 && Object.keys(message.data[0]).map((header) => (
                              <th key={header} className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                {header}
                              </th>
                            ))}
                          </thead>
                          <tbody className="bg-white divide-y divide-gray-200">
                            {message.data.map((row, i) => (
                              <tr key={i}>
                                {Object.values(row).map((cell, j) => (
                                  <td key={j} className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                                    {String(cell)}
                                  </td>
                                ))}
                              </tr>
                            ))}
                          </tbody>
                        </table>
                      </div>
                    </div>
                  )}
                </div>
              </div>
            ))}
            {isLoading && (
              <div className="flex items-center space-x-2 text-gray-500">
                <div className="w-2 h-2 bg-gray-500 rounded-full animate-bounce"></div>
                <div className="w-2 h-2 bg-gray-500 rounded-full animate-bounce" style={{ animationDelay: '0.2s' }}></div>
                <div className="w-2 h-2 bg-gray-500 rounded-full animate-bounce" style={{ animationDelay: '0.4s' }}></div>
              </div>
            )}
            <div ref={messagesEndRef} />
          </div>

          {/* Input Area */}
          <div className="border-t p-4">
            <form onSubmit={handleSubmit} className="flex space-x-4">
              <input
                type="text"
                value={input}
                onChange={(e) => setInput(e.target.value)}
                placeholder="Ask a question about your data..."
                className="flex-1 rounded-lg border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                disabled={isLoading}
              />
              <button
                type="submit"
                disabled={isLoading}
                className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-lg shadow-sm text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
              >
                <ChevronRightIcon className="h-5 w-5" />
              </button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
