import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';
import { ChevronRightIcon, CodeBracketIcon, ChatBubbleLeftIcon, BeakerIcon } from '@heroicons/react/24/outline';

function MovingBubbles() {
  return (
    <div className="fixed inset-0 overflow-hidden pointer-events-none">
      <div className="absolute inset-0 bg-gradient-to-br from-purple-900 via-violet-800 to-indigo-900">
        {[...Array(6)].map((_, i) => (
          <div
            key={i}
            className="absolute rounded-full mix-blend-soft-light"
            style={{
              background: `radial-gradient(circle at center, ${['#ff69b4', '#9370db', '#8a2be2'][i % 3]}, transparent)`,
              width: `${Math.random() * 400 + 200}px`,
              height: `${Math.random() * 400 + 200}px`,
              left: `${Math.random() * 100}%`,
              top: `${Math.random() * 100}%`,
              animation: `float-${i} ${Math.random() * 10 + 20}s infinite ease-in-out`,
            }}
          />
        ))}
      </div>
    </div>
  );
}

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  useEffect(() => {
    // Add keyframe animations for bubbles
    const style = document.createElement('style');
    const keyframes = [...Array(6)].map((_, i) => `
      @keyframes float-${i} {
        0% { transform: translate(0, 0) scale(1); opacity: ${Math.random() * 0.5 + 0.3}; }
        33% { transform: translate(${Math.random() * 100 - 50}px, ${Math.random() * 100 - 50}px) scale(${Math.random() * 0.5 + 0.8}); opacity: ${Math.random() * 0.5 + 0.3}; }
        66% { transform: translate(${Math.random() * 100 - 50}px, ${Math.random() * 100 - 50}px) scale(${Math.random() * 0.5 + 0.8}); opacity: ${Math.random() * 0.5 + 0.3}; }
        100% { transform: translate(0, 0) scale(1); opacity: ${Math.random() * 0.5 + 0.3}; }
      }
    `).join('');
    style.textContent = keyframes;
    document.head.appendChild(style);
    return () => document.head.removeChild(style);
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;

    const userMessage = { type: 'user', content: input };
    setMessages(prev => [...prev, userMessage]);
    setInput('');
    setIsLoading(true);

    try {
      const response = await axios.post('http://localhost:8000/api/chat', {
        message: input
      });

      const botMessage = {
        type: 'bot',
        content: response.data.answer,
        sql: response.data.sql_query,
        data: response.data.data,
        showSql: false
      };

      setMessages(prev => [...prev, botMessage]);
    } catch (error) {
      const errorMessage = {
        type: 'error',
        content: 'Sorry, there was an error processing your request.'
      };
      setMessages(prev => [...prev, errorMessage]);
    }

    setIsLoading(false);
  };

  const toggleSql = (index) => {
    setMessages(messages.map((msg, i) => {
      if (i === index) {
        return { ...msg, showSql: !msg.showSql };
      }
      return msg;
    }));
  };

  return (
    <div className="min-h-screen relative">
      <MovingBubbles />

      {/* Content */}
      <div className="relative z-10">
        {/* Header */}
        <div className="bg-black/20 backdrop-blur-lg border-b border-white/10">
          <div className="max-w-7xl mx-auto px-4 py-6">
            <div className="flex items-center space-x-3">
              <BeakerIcon className="h-8 w-8 text-pink-400" />
              <div>
                <h1 className="text-3xl font-bold text-white">Talk2Tables</h1>
                <p className="text-purple-200/70 text-sm">Chat with your database using natural language</p>
              </div>
            </div>
          </div>
        </div>

        {/* Main Content */}
        <div className="max-w-7xl mx-auto px-4 py-8">
          <div className="bg-black/20 backdrop-blur-md rounded-xl shadow-2xl min-h-[600px] flex flex-col border border-white/10">
            {/* Messages Area */}
            <div className="flex-1 p-6 overflow-y-auto space-y-6">
              {messages.length === 0 && (
                <div className="text-center py-12">
                  <ChatBubbleLeftIcon className="h-12 w-12 text-purple-300/30 mx-auto mb-4" />
                  <h3 className="text-lg font-medium text-purple-100/70">No messages yet</h3>
                  <p className="text-purple-200/50 text-sm mt-2">Start by asking a question about your data</p>
                </div>
              )}
              {messages.map((message, index) => (
                <div key={index} className={`flex ${message.type === 'user' ? 'justify-end' : 'justify-start'}`}>
                  <div className={`max-w-[80%] backdrop-blur-sm ${
                    message.type === 'user' 
                      ? 'bg-gradient-to-br from-pink-500/80 to-purple-600/80 text-white rounded-2xl rounded-tr-sm' 
                      : message.type === 'error'
                      ? 'bg-gradient-to-br from-red-500/80 to-red-600/80 text-white rounded-2xl rounded-tl-sm'
                      : 'bg-gradient-to-br from-purple-600/40 to-indigo-700/40 text-purple-50 rounded-2xl rounded-tl-sm'
                  } p-4 shadow-lg border border-white/10`}>
                    <p className="text-sm md:text-base">{message.content}</p>

                    {message.type === 'bot' && message.data && (
                      <div className="mt-4 space-y-4">
                        <button
                          onClick={() => toggleSql(index)}
                          className="flex items-center space-x-2 text-sm text-purple-200 hover:text-white transition-colors"
                        >
                          <CodeBracketIcon className="h-4 w-4" />
                          <span>{message.showSql ? 'Hide SQL' : 'Show SQL'}</span>
                        </button>

                        {message.showSql && (
                          <pre className="mt-2 p-3 bg-black/30 text-purple-200 rounded-lg text-sm overflow-x-auto font-mono border border-white/10">
                            {message.sql}
                          </pre>
                        )}

                        <div className="mt-4 overflow-x-auto">
                          <div className="inline-block min-w-full align-middle">
                            <div className="overflow-hidden border border-white/10 rounded-lg bg-black/20">
                              <table className="min-w-full divide-y divide-white/10">
                                <thead className="bg-black/30">
                                  {message.data.length > 0 && Object.keys(message.data[0]).map((header) => (
                                    <th key={header} className="px-4 py-3 text-left text-xs font-medium text-purple-200 uppercase tracking-wider">
                                      {header}
                                    </th>
                                  ))}
                                </thead>
                                <tbody className="divide-y divide-white/10">
                                  {message.data.map((row, i) => (
                                    <tr key={i} className="hover:bg-white/5 transition-colors">
                                      {Object.values(row).map((cell, j) => (
                                        <td key={j} className="px-4 py-3 whitespace-nowrap text-sm text-purple-100">
                                          {String(cell)}
                                        </td>
                                      ))}
                                    </tr>
                                  ))}
                                </tbody>
                              </table>
                            </div>
                          </div>
                        </div>
                      </div>
                    )}
                  </div>
                </div>
              ))}
              {isLoading && (
                <div className="flex items-center justify-center space-x-2 text-purple-300">
                  <div className="w-2 h-2 bg-current rounded-full animate-bounce"></div>
                  <div className="w-2 h-2 bg-current rounded-full animate-bounce" style={{ animationDelay: '0.2s' }}></div>
                  <div className="w-2 h-2 bg-current rounded-full animate-bounce" style={{ animationDelay: '0.4s' }}></div>
                </div>
              )}
              <div ref={messagesEndRef} />
            </div>

            {/* Input Area */}
            <div className="border-t border-white/10 p-4 bg-black/20 backdrop-blur-md rounded-b-xl">
              <form onSubmit={handleSubmit} className="flex space-x-4">
                <input
                  type="text"
                  value={input}
                  onChange={(e) => setInput(e.target.value)}
                  placeholder="Ask a question about your data..."
                  className="flex-1 bg-black/20 text-purple-100 rounded-lg px-4 py-2 border border-white/10 focus:border-pink-500/50 focus:ring-2 focus:ring-pink-500/20 focus:outline-none placeholder-purple-300/50 backdrop-blur-sm"
                  disabled={isLoading}
                />
                <button
                  type="submit"
                  disabled={isLoading}
                  className="inline-flex items-center px-4 py-2 border border-white/10 text-sm font-medium rounded-lg text-white bg-gradient-to-r from-pink-500/80 to-purple-600/80 hover:from-pink-500/90 hover:to-purple-600/90 focus:outline-none focus:ring-2 focus:ring-pink-500/50 disabled:opacity-50 disabled:cursor-not-allowed transition-all backdrop-blur-sm"
                >
                  <ChevronRightIcon className="h-5 w-5" />
                </button>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
