import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';
import { ChevronRightIcon, CodeBracketIcon, ChatBubbleLeftIcon, TableCellsIcon, BeakerIcon } from '@heroicons/react/24/outline';

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;

    const userMessage = { type: 'user', content: input };
    setMessages(prev => [...prev, userMessage]);
    setInput('');
    setIsLoading(true);

    try {
      const response = await axios.post('http://localhost:8000/api/chat', {
        message: input
      });

      const botMessage = {
        type: 'bot',
        content: response.data.answer,
        sql: response.data.sql_query,
        data: response.data.data,
        showSql: false
      };

      setMessages(prev => [...prev, botMessage]);
    } catch (error) {
      const errorMessage = {
        type: 'error',
        content: 'Sorry, there was an error processing your request.'
      };
      setMessages(prev => [...prev, errorMessage]);
    }

    setIsLoading(false);
  };

  const toggleSql = (index) => {
    setMessages(messages.map((msg, i) => {
      if (i === index) {
        return { ...msg, showSql: !msg.showSql };
      }
      return msg;
    }));
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 via-gray-800 to-gray-900">
      {/* Header */}
      <div className="bg-gray-800 border-b border-gray-700 shadow-xl">
        <div className="max-w-7xl mx-auto px-4 py-6">
          <div className="flex items-center space-x-3">
            <BeakerIcon className="h-8 w-8 text-blue-500" />
            <div>
              <h1 className="text-3xl font-bold text-white">Talk2Tables</h1>
              <p className="text-gray-400 text-sm">Chat with your database using natural language</p>
            </div>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="max-w-7xl mx-auto px-4 py-8">
        <div className="bg-gray-800 rounded-xl shadow-2xl min-h-[600px] flex flex-col border border-gray-700">
          {/* Messages Area */}
          <div className="flex-1 p-6 overflow-y-auto space-y-6">
            {messages.length === 0 && (
              <div className="text-center py-12">
                <ChatBubbleLeftIcon className="h-12 w-12 text-gray-600 mx-auto mb-4" />
                <h3 className="text-lg font-medium text-gray-400">No messages yet</h3>
                <p className="text-gray-500 text-sm mt-2">Start by asking a question about your data</p>
              </div>
            )}
            {messages.map((message, index) => (
              <div key={index} className={`flex ${message.type === 'user' ? 'justify-end' : 'justify-start'}`}>
                <div className={`max-w-[80%] ${
                  message.type === 'user' 
                    ? 'bg-blue-600 text-white rounded-2xl rounded-tr-sm' 
                    : message.type === 'error'
                    ? 'bg-red-600 text-white rounded-2xl rounded-tl-sm'
                    : 'bg-gray-700 text-gray-100 rounded-2xl rounded-tl-sm'
                } p-4 shadow-lg`}>
                  <p className="text-sm md:text-base">{message.content}</p>

                  {message.type === 'bot' && message.data && (
                    <div className="mt-4 space-y-4">
                      <button
                        onClick={() => toggleSql(index)}
                        className="flex items-center space-x-2 text-sm text-gray-300 hover:text-white transition-colors"
                      >
                        <CodeBracketIcon className="h-4 w-4" />
                        <span>{message.showSql ? 'Hide SQL' : 'Show SQL'}</span>
                      </button>

                      {message.showSql && (
                        <pre className="mt-2 p-3 bg-gray-900 text-gray-300 rounded-lg text-sm overflow-x-auto font-mono">
                          {message.sql}
                        </pre>
                      )}

                      <div className="mt-4 overflow-x-auto">
                        <div className="inline-block min-w-full align-middle">
                          <div className="overflow-hidden border border-gray-700 rounded-lg">
                            <table className="min-w-full divide-y divide-gray-700">
                              <thead className="bg-gray-900">
                                {message.data.length > 0 && Object.keys(message.data[0]).map((header) => (
                                  <th key={header} className="px-4 py-3 text-left text-xs font-medium text-gray-400 uppercase tracking-wider">
                                    {header}
                                  </th>
                                ))}
                              </thead>
                              <tbody className="bg-gray-800 divide-y divide-gray-700">
                                {message.data.map((row, i) => (
                                  <tr key={i} className="hover:bg-gray-750">
                                    {Object.values(row).map((cell, j) => (
                                      <td key={j} className="px-4 py-3 whitespace-nowrap text-sm text-gray-300">
                                        {String(cell)}
                                      </td>
                                    ))}
                                  </tr>
                                ))}
                              </tbody>
                            </table>
                          </div>
                        </div>
                      </div>
                    </div>
                  )}
                </div>
              </div>
            ))}
            {isLoading && (
              <div className="flex items-center justify-center space-x-2 text-gray-400">
                <div className="w-2 h-2 bg-current rounded-full animate-bounce"></div>
                <div className="w-2 h-2 bg-current rounded-full animate-bounce" style={{ animationDelay: '0.2s' }}></div>
                <div className="w-2 h-2 bg-current rounded-full animate-bounce" style={{ animationDelay: '0.4s' }}></div>
              </div>
            )}
            <div ref={messagesEndRef} />
          </div>

          {/* Input Area */}
          <div className="border-t border-gray-700 p-4 bg-gray-850">
            <form onSubmit={handleSubmit} className="flex space-x-4">
              <input
                type="text"
                value={input}
                onChange={(e) => setInput(e.target.value)}
                placeholder="Ask a question about your data..."
                className="flex-1 bg-gray-700 text-gray-100 rounded-lg px-4 py-2 border border-gray-600 focus:border-blue-500 focus:ring-2 focus:ring-blue-500 focus:outline-none placeholder-gray-400"
                disabled={isLoading}
              />
              <button
                type="submit"
                disabled={isLoading}
                className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-lg text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
              >
                <ChevronRightIcon className="h-5 w-5" />
              </button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
